package com.syntax.class02;

public class VariableValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte boxNum1;
		boxNum1 = 1;
		short boxNum2;
		boxNum2 = 23;
		int boxNum3;
		boxNum3 = 456;
		long boxNum4;
		boxNum4 = 7890000000000000L;
		
		System.out.println("My byte value: " + boxNum1);
		System.out.println("My short value: " + boxNum2);
		System.out.println("My integer value: " + boxNum3);
		System.out.println("My long value: " + boxNum4);

		float boxFlat;
		boxFlat = 0.99f;
		double boxDouble;
		boxDouble = 1.234567980;
		System.out.println("My float value: " + boxFlat);
		System.out.println("My double value: " + boxDouble);
		
		char boxChar;
		boxChar = 'M';
		System.out.println("My Char: " + boxChar);
		
		boolean boxBoolean1;
		boxBoolean1 = true;
		boolean boxBoolean2;
		boxBoolean2 = false;
		System.out.println("Boolean True: " + boxBoolean1);
		System.out.println("Boolean False: " + boxBoolean2);

		
		
	}

}
