package com.syntax.class08;

public class NestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int a=0; a<=3; a++) {
			System.out.println("I aam in outer loop");
			for (int b=0; b<=3; b++) {
				System.out.println("I am in inner loop");
			}
		}
	
	
	
	
	}

}
